package p2023_07_14;

public class For01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//메세지를 10번 출력하는 프로그램
		//실행될 문장이 1줄인 경우에는 중괄호{}를 생략할 수 있다.
		for(int i = 1; i<=10; i++) {//증감식은 i++, ++i, i=i+1, i+=1로 작성가능
			System.out.println(i + " 푸하하 !! >💙<");
		}

	}

}
